advanced.example:
# Simple decorator
def uppercase_decorator(func):
    def wrapper():
        result = func()
        return result.upper()
    return wrapper

@uppercase_decorator
def greet():
    return 'hello, world'

print(greet())  # HELLO, WORLD

# Decorator with arguments
def repeat(times):
    def decorator(func):
        def wrapper(*args, **kwargs):
            for _ in range(times):
                func(*args, **kwargs)
        return wrapper
    return decorator

@repeat(3)
def say_hello():
    print('Hello!')

say_hello()

exercise.solution:
def double_result(func):
    def wrapper():
        return func() * 2
    return wrapper

@double_result
def get_number():
    return 5

print(get_number())

advanced.example:
# Simple generator
def count_up_to(n):
    count = 1
    while count <= n:
        yield count
        count += 1

# Using the generator
for num in count_up_to(5):
    print(num)

# Generator expression
squares = (x ** 2 for x in range(5))
for square in squares:
    print(square)

# Fibonacci generator
def fibonacci(n):
    a, b = 0, 1
    for _ in range(n):
        yield a
        a, b = b, a + b

for num in fibonacci(7):
    print(num)

exercise.solution:
def even_numbers():
    for i in range(2, 11, 2):
        yield i

for num in even_numbers():
    print(num)
